	// Get the table by its ID
const table = document.getElementById("myTable");

// Specify the column index (0-based). For example, column 1 = index 0.
const columnIndex = 1; // Change this to your desired column (e.g., 0 for the first column)

// Get all rows in the 
const tbody = table.querySelector("tbody");
const rows = tbody.rows;

// Array to store the data from the specified column
const columnData = [];

// Loop through each row and get the  at the specified index
for (let i = 0; i < rows.length; i++) {
  const cell = rows[i].cells[columnIndex]; // Get the  at columnIndex
  if (cell) {
    columnData.push(cell.textContent.trim()); // Add the cell's text content to the array
  }
}

// Output the data
console.log(`Data from column ${columnIndex + 1}:`, columnData);

