// Get the table by its ID
const table = document.getElementById("myTable");

// Specify the column indices you want to extract (0-based)
const columnIndices = [0, 2]; // Example: Get data from the 1st and 3rd columns

// Get all rows in the 
const tbody = table.querySelector("tbody");
const rows = tbody.rows;

// Array to store data from the specified columns
const columnData = [];

// Loop through each row
for (let i = 0; i < rows.length; i++) {
  const row = rows[i];
  const rowData = {}; // Object to store data for the current row

  // Loop through the specified column indices
  columnIndices.forEach((colIndex) => {
    const cell = row.cells[colIndex]; // Get the  at the column index
    if (cell) {
      rowData[`Column ${colIndex + 1}`] = cell.textContent.trim(); // Add cell data
    }
  });

  // Add the row data object to the array
  columnData.push(rowData);
}

// Output the data
console.log("Data from specified columns:", columnData);


