project.parent:
		org.springframework.boot
		spring-boot-starter-parent
		3.1.0



		pipeline {
    agent any
    parameters {
        string(
            name: 'PUBLIC_IDS', 
            defaultValue: 'a5werw,bxgsfsd,c4sdff', 
            description: 'Comma-separated public IDs (e.g., a5werw,bxgsfsd,c4sdff)'
        )
    }
    stages {
        stage('Setup parameters') {
            steps {
                script {
                    // Access the parameter
                    def publicIds = params.PUBLIC_IDS
                    echo "Provided public IDs: ${publicIds}"

                    // Use the parameter in a URL
                    def baseURL = "http://example.com"
                    def url = "${baseURL}?public_ids=${publicIds.replace(',', '%2C')}"
                    echo "Constructed URL: ${url}"
                }
            }
        }
    }
}
